mapper.update:
UPDATE user
SET nickname=#{nickname}, fit_type=#{fitType}, goal=#{goal}
WHERE email=#{email}

mapper.select:
SELECT nickname FROM user u JOIN board b ON u.user_seq = b.user_seq where board_seq in
#{board.boardSeq}
order by board_seq desc

mapper.update:
UPDATE user
SET password=#{password}
WHERE email=#{email}
